<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Symfony\Component\HttpFoundation\File\UploadedFile; use Image; class Photo extends Model{    protected $fillable = array('path', 'name', 'thumbnail_path');    protected $table = 'flyer_photos';     protected $baseDir = 'flyer/photos';        /**     * [flyer description]     * @return [type] [description]     */    public function flyer()    {    	return $this->belongsTo('App\Flyer');     }    public static function named($name)    {       return (new static)->saveAs($name);     }    protected function saveAs($name)    {        $this->name = sprintf("%s-%s", time(), $name);         $this->path = sprintf("%s/%s", $this->baseDir, $this->name);         $this->thumbnail_path = sprintf("%s/tn-%s", $this->baseDir, $this->name);                 return $this;      }    public function move(UploadedFile $file)    {        $file->move($this->baseDir, $this->name);         $this->makeThumbnail();                 return $this;     }    public function makeThumbnail()    {        Image::make($this->path)        ->fit(200)        ->save($this->thumbnail_path);         return $this;     }}