<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Symfony\Component\HttpFoundation\File\UploadedFile; use Image; use App\Flyer; class Photo extends Model{    protected $fillable = array('path', 'name', 'thumbnail_path');    protected $table = 'flyer_photos';     //protected $file;       /**     * [flyer description]     * @return [type] [description]     */    public function flyer()    {    	return $this->belongsTo('App\Flyer', 'flyer_id');     }      public function setNameAttribute($name)    {        $this->attributes['name'] = $name;         $this->path = $this->baseDir() .'/'.$name;         $this->thumbnail_path = $this->baseDir() .'/tn-'.$name;     }    public function baseDir()    {        return 'flyer/photos';     }   }